mapper.select:
SELECT S.SALES_OPPT_ID, S.SALES_OPPT_NM, (SELECT C.CD_NM FROM CODE C WHERE C.CD_GRP_ID='OTL' AND C.CODE = S.SALES_LEV_CD) SALES_LEV_CD, (SELECT C.CD_NM FROM CODE C WHERE C.CD_GRP_ID='OSC' AND C.CODE = S.SALES_OPPT_STAT_CD ) SALES_OPPT_STAT_CD, S.EXPT_SALES_AMT, S.EXPT_FIN_D, S.PSBLTY_RATE FROM SALES_OPPORTUNITY S WHERE S.ACT_YN = 'Y' AND S.SALES_OPPT_STAT_CD = '0001'
AND S.CUST_ID = #{cust_id}
ORDER BY SALES_OPPT_ID

mapper.update:
      UPDATE SALES_ACTIVITY 
	  SET ACT_YN = 'Y', FIN_MDFY_ID = #{fin_mdfy_id}, FIN_MDFY_DT = sysdate
	  WHERE SALES_ACTVY_ID = #{sales_actvy_id}

UPDATE SALES_ACTIVITY
SET ACT_YN = 'N', FIN_MDFY_ID = #{fin_mdfy_id}, FIN_MDFY_DT = sysdate
WHERE SALES_ACTVY_ID = #{sales_actvy_id}

INSERT INTO PROD_OF_SALES_OPPT
  (
    SALES_OPPT_ID,
    PROD_ID,
    PROD_QTY,
    PROD_PRICE,
    SUP_PRICE,
    DISCOUNT,
    DISCOUNT_UNIT_CD,
    FST_REG_ID,
    FIN_MDFY_ID
  )
  VALUES
  (
	#{sales_oppt_id}
    ,#{prod_id}
    ,#{estim_qty}
    ,#{sales_price}
    ,#{sup_price}
    ,#{discount}
    ,#{discount_unit_cd}
    ,'admin'
    ,'admin'
  )

UPDATE sales_opportunity
SET act_yn = 'N'
WHERE sales_oppt_id = #{opptId}
