mapper.select:
SELECT A.AUTH_ID, (SELECT AUTH_NM FROM AUTHORIZATION WHERE AUTH_ID=A.AUTH_ID)AUTH_NM
FROM AUTHORIZATION_IUSER a
WHERE IUSER_ID =  #{iuser_id}

SELECT count(*)
FROM AUTHORIZATION_IUSER
WHERE IUSER_ID = #{iuser_id}

mapper.update:
UPDATE AUTHORIZATION_IUSER
SET AUTH_ID=#{auth_id}
WHERE IUSER_ID=#{iuser_id}
