mapper.update:
UPDATE AUTHORIZATION_MENU
SET CREAT_YN = #{creat_yn}, RETRV_YN = #{retrv_yn}
,MDFY_YN = #{mdfy_yn}, DEL_YN = #{del_yn}, DEFLT_YN = #{deflt_yn}       
WHERE AUTH_ID = #{auth_id}
AND MENU_ID = #{menu_id}

mapper.delete:
delete from AUTHORIZATION_MENU
WHERE AUTH_ID = #{auth_id}
and MENU_ID = #{menu_id}
