mapper.select:
		select count(*) as cnt
		FROM TB_BOARD
		where ACTIVE_FLG='Y' AND BOARD_MNG_NO = #{BOARD_MNG_NO}
		
			AND ANSWER_FLG = #{qna_answer}
		

		
			AND TITLE LIKE '%'||#{keyword}||'%'

SELECT b.*
FROM (
SELECT ROWNUM seq, a.*
from(
SELECT
BOARD_NO, BOARD_MNG_NO, FILE_CD, TITLE, CONTENT, VIEW_CNT, QUESTION_TYPE_CD, ANSWER_FLG, ACTIVE_FLG, DEL_FLG, CREATED_BY, CREATED, UPDATED_BY, UPDATED
FROM tb_board
WHERE ACTIVE_FLG='Y' and BOARD_MNG_NO = #{BOARD_MNG_NO}

	AND title like '%'||#{keyword}||'%'
 
ORDER BY BOARD_NO DESC
) a
) b
WHERE seq >= #{page.startRow} and seq <= #{page.endRow}

SELECT COUNT(*) FROM tb_board where ACTIVE_FLG ='Y' and BOARD_MNG_NO = #{BOARD_MNG_NO}
AND title like '%'||#{title}||'%'
AND content like '%'||#{content}||'%'
AND ANSWER_FLG = #{qna_answer}
AND TITLE LIKE '%'||#{keyword}||'%'

		select X.*
		from (
		select rownum as rnum, A.*
		from (
		SELECT
		BOARD_NO, BOARD_MNG_NO, FILE_CD,   TITLE, CONTENT,  VIEW_CNT, CREATED_BY, CREATED, QUESTION_TYPE_CD, ANSWER_FLG, ACTIVE_FLG, DEL_FLG
		FROM tb_board
		WHERE ACTIVE_FLG='Y' AND BOARD_MNG_NO = #{BOARD_MNG_NO}
		ORDER BY BOARD_NO DESC
		) A
		) X
 
		 where X.rnum >= #{page.startRow} and X.rnum <= #{page.endRow}

		select X.*
		from (
		select rownum as rnum, A.*
		from (
		SELECT
		A.BOARD_NO, A.QUESTION_TYPE_CD, A.ANSWER_FLG, A.CREATED_BY,
		A.CREATED, B.CD_NM, A.TITLE, A.VIEW_CNT, A.BOARD_MNG_NO
		FROM TB_BOARD A, CODE B
		WHERE ACTIVE_FLG = 'Y' AND BOARD_MNG_NO = #{BOARD_MNG_NO} AND B.CD_GRP_ID = 'QAT' AND
		A.QUESTION_TYPE_CD = B.CODE
		
			AND ANSWER_FLG = #{qna_answer}
		

		
			AND TITLE LIKE '%'||#{keyword}||'%'
		
		ORDER BY A.BOARD_NO DESC 
		) A
		) X
 
		 where X.rnum >= #{page.startRow} and X.rnum <= #{page.endRow}

mapper.update:
 
		update tb_board set title = #{TITLE}, content = #{CONTENT}, updated_by = #{UPDATED_BY}, file_cd = (SELECT FILE_CD FROM TB_FILE
		WHERE FILE_NM =#{FILE_NM} and CREATED = sysdate), UPDATED = sysdate
		where BOARD_NO = #{BOARD_NO}

mapper.select:
SELECT COUNT(*) AS CNT FROM TB_BOARD WHERE ACTIVE_FLG = 'Y'
AND ANSWER_FLG = #{qna_answer}
AND TITLE LIKE '%'||#{keyword}||'%'

SELECT X.* FROM ( SELECT ROWNUM AS RNUM, A.* FROM ( SELECT * FROM TB_BOARD WHERE ACTIVE_FLG = 'Y' and BOARD_MNG_NO ='BMG1000003'
AND ANSWER_FLG = #{qna_answer}
AND TITLE LIKE '%' || #{keyword} || '%'
ORDER BY BOARD_NO DESC ) A ) X
WHERE X.RNUM >= #{startRow} AND X.RNUM <= #{endRow}

SELECT X.* FROM ( SELECT ROWNUM AS RNUM, A.* FROM (
SELECT A.BOARD_NO, A.TITLE, A.QUESTION_TYPE_CD, A.ANSWER_FLG, A.CREATED_BY, B.CODE_TXT, A.CREATED, A.VIEW_CNT FROM TB_BOARD A, TB_CODE_DETAIL B WHERE ACTIVE_FLG = 'Y' AND BOARD_MNG_NO = 'BMG1000003' AND A.QUESTION_TYPE_CD = B.CODE1
AND ANSWER_FLG = #{qna_answer}
AND TITLE LIKE '%' || #{keyword} || '%'
ORDER BY BOARD_NO DESC ) A ) X
WHERE X.RNUM >= #{startRow} AND X.RNUM <= #{endRow}

SELECT X.* FROM ( SELECT ROWNUM AS RNUM, A.* FROM ( SELECT BOARD_NO, TITLE, QUESTION_TYPE_CD, ANSWER_FLG, CREATED_BY, to_char(CREATED,'yy-mm-dd hh:mi') CREATED, VIEW_CNT FROM TB_BOARD WHERE ACTIVE_FLG = 'Y' AND BOARD_MNG_NO = 'BMG1000002'
AND ANSWER_FLG = #{qna_answer}
AND TITLE LIKE '%' || #{keyword} || '%'
ORDER BY BOARD_NO DESC ) A ) X
WHERE X.RNUM >= #{startRow} AND X.RNUM <= #{endRow}

mapper.insert:
insert into TB_FILE (FILE_CD, FILE_NO, FILE_PATH, FILE_NM, FILE_EXT, CREATED_BY) values ( ( SELECT /*+INDEX_DESC(TB_FILE TB_FILE)*/ DECODE(MAX(FILE_CD), NULL, 'FIL1000001', SUBSTR(MAX(FILE_CD), 1, 3) || (SUBSTR(MAX(FILE_CD), 4, 7)+1)) FILE_CD FROM TB_FILE
WHERE FILE_CD < CONCAT('FIL', '9999999') AND ROWNUM = 1
), FILE_SEQ.NEXTVAL, #{FILE_PATH}, #{FILE_NM}, #{FILE_EXT}, 'ADMIN' )

mapper.select:
SELECT *
FROM TB_FILE
WHERE FILE_CD=#{FILE_CD}
