mapper.select:
    	SELECT count(*)
		FROM ORGANIZATION_TYPE
		WHERE ORG_TYPE_NM = #{org_nm}

    	select count(*)
		from organization
		where org_nm = #{org_nm}

SELECT ORG_TYPE_ID
FROM ORGANIZATION_TYPE
WHERE ORG_TYPE_NM = #{org_type_nm}

SELECT ORG_ID
FROM ORGANIZATION
WHERE ORG_NM = #{org_nm}
AND ORG_TYPE_ID = #{org_type_id}
