mapper.select:
SELECT SAL.* FROM ( SELECT rownum num, a.* FROM ( SELECT p.prod_id, p.prod_nm ,cd.cd_nm ,cd.code ,p.prod_price ,c1.CATE_ID || '>' || c2.UP_CATE_ID as cate ,p.fst_reg_id ,p.fst_reg_dt FROM PRODUCT p, code cd,category c1,category c2 WHERE c1.cate_id = c2.cate_id AND p.cate_id = c1.CATE_ID AND p.PROD_DIV_CD = cd.code AND cd.cd_grp_id = 'PRD' AND p.act_yn = 'Y'
AND p.prod_nm like '%' || #{prod_nm} || '%'
AND cd.code like #{code}
order by p.prod_id desc ) a order by rownum ) SAL WHERE SAL.num>=#{startRow} AND SAL.num<=#{endRow}

mapper.update:
UPDATE product SET CATE_ID = #{cate_id}, PROD_NM = #{prod_nm}, PROD_DIV_CD = #{prod_div_cd}, prod_price = #{prod_price}, PROD_URL = #{prod_url}, PROD_DTL_CONT = #{prod_dtl_cont},
fin_mdfy_id = #{fst_reg_id}, FIN_MDFY_DT = sysdate
WHERE PROD_ID=#{prod_id}

mapper.delete:
delete from PROD_OF_ATTACH
where prod_id = #{prod_id}
	and ATTACH_ID = #{attach_img_id}

delete from PROD_OF_ATTACH
where prod_id = #{prod_id}
	and ATTACH_ID = #{attach_catal_id}
